package javatutorial;

public class IfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int number = 54;
        
        if (number<0) {
        	System.out.println("negative");
        }
        else if(number>0){
        		System.out.println("positive");
        }
        else {
        	System.out.println("zero");
        }
        
	}
	}
